er.ListenSendMessage(convertMessage(message), convertBalances(balances));        // Log the response gotten back                log.logResponse(messageFormat.getStatus());                return messageFormat;    }        // Log into which to record messages    	private BroadcastBalances convertBalances(Balances balances)     {    	BroadcastBalances broadcastBalances = new BroadcastBalances();    	broadcastBalances.setBalances(balances.getTotal(), balances.getAvailable());    	return broadcastBalances;	}	private BroadcastMessage convertMessage(Message message)     {    	return new BroadcastMessage(message.getMessageCode(), message.getCard(), message.getPIN(), message.getSerialNumber(), message.getFromAccount(), message.getToAccount(), message.getAmount());	}	    private EventBus eventBus;    	public void setEventBus(EventBus eventBus) 	{		this.eventBus = eventBus;	}	private Log log;        // Network address of the bank        private InetAddress bankAddress;}