    this.atm = atm;                // Create the simulated individual components of the ATM's GUI                operatorPanel = new SimOperatorPanel(this);        cardReader = new SimCardReader(this);        display = new SimDisplay();        cashDispenser = new SimCashDispenser();        envelopeAcceptor = new SimEnvelopeAcceptor();        receiptPrinter = new SimReceiptPrinter();        keyboard = new SimKeyboard(display, envelopeAcceptor);                // Create the GUI containing the above                gui = new GUI(operatorPanel, cardReader, display, keyboard,                      cashDispenser, envelopeAcceptor, receiptPrinter);                // Create the edu.gordon.simulation of the bank                simulatedBank = new SimulatedBank();                theInstance = this;                eventBus = new EventBus();        eventBus.register(new AcceptEnvelopeComponent());        eventBus.register(new ReadCardComponent());        eventBus.register(new GetInitialCashComponent());        eventBus.register(new SendMessageComponent());        eventBus.register(new ReadMenuChoiceComponent());        eventBus.register(new ReadPINComponent());        eventBus.register(new ReadAmountComponent());    }        /** Accessor for the one and only instance of this class     *     *  @return the instance of this class     */    public static Simulation getInstance()    {        return theInstance;    }        /** Simulated getting initial amount of cash from operator     *     *  @return value of initial cash entered     */      public static void getInitialCash()    {    	eventBus.post(new GetInitialCashEvent(gui.getInitialCash()));    }        /** Simulate reading of a card     *     *     *  @return Card object representing information on the card if read     *          successfully, null if not read successfully     */    public static void readCard()    {    	atm.consumePhysicalState();        // Machine can't be turned off while there is a card in it        operatorPanel.setEnabled(false);        cardReader.animateInsertion();                // Since we don't have a magnetic stripe reader, we'll simulate by        // having customer type the card number in        BroadcastCard card = gui.readCard();        while (card == null)        {        	card = gui.readCard();        }             eventBus.post(new ReadCardEvent(card));    }        /** Simulate ejecting a card      */    public static void ejectCard()    {        cardReader.animateEjection();        // Re-enable on-off switch        operatorPanel.setEnabled(true);    }    public static void retainCard()    {        cardReader.animateRetention();        // Re-enable on-off switch        operatorPanel.setEnabled(true);    }        /** Clear the simulated display     */    public static void clearDisplay()    {        display.clearDisplay();    }        /** Write one or more lines to the display - beginning just after the     *  last line written     *     *  @param text the text to display     */    public static void displayMessage(String message)    {    	clearDisplay();    	display(message);    }        public static void display(String text)    {        display.display(text);    }         /** Simulate reading input from the keyboard     *     *  @param mode the input mode to use - one of the constants defined below.     *  @param maxValue the maximum acceptable value (used in MENU_MODE only)     *  @return the line that was entered - null if user pressed CANCEL.     */    public static void readPIN(String prompt, int maxValue)    {        clearDisplay();        display(prompt);        display("");                   String input = readInput(Simulation.PIN_MODE, maxValue);        if (input == null)        {        	input = "-99"; //This will cause a cancel exception on the other side.        }                clearDisplay();        eventBus.post(new ReadPINEvent("", input));    }        public static void readMenuChoice(String prompt, String[] menu, int maxValue)    {		clearDisplay();		display(prompt);		for (int i = 0; i < menu.length; i ++)		{			display((i+1) + ") " + menu[i]);		}		        String input = readInput(Simulation.MENU_MODE, maxValue);        if (input == null)        {        	input = "-99"; //This will cause a cancel exception on the other side.        }                clearDisplay();        eventBus.post(new ReadMenuChoiceEvent(input));    }        public static void readAmount(String prompt, int maxValue)    {	   clearDisplay();       display(prompt);       display("");       String input = readInput(Simulation.AMOUNT_MODE, maxValue);       clearDisplay();       eventBus.post(new ReadAmountEvent(input));    }        public static String readInput(int mode, int maxValue)    {        return keyboard.readInput(mode, maxValue);    }        /** Simulate dispensing cash to a customer     *     *  @param amount the amount of cash to dispense     *     *  Precondition: amount is <= cash on hand     */      public static void dispenseCash(BroadcastMoney amount)    {        cashDispenser.animateDispensingCash(amount);    }    /** Simulate accepting an envelope from customer.     *     *  return true if an envelope was received within the prescribed time,     *         else false     */    public static void acceptEnvelope()    {    	eventBus.post(new AcceptEnvelopeEvent(Boolean.toString(envelopeAcceptor.acceptEnvelope())));    }    /** Simulate printing one line of a receipt     *     *  @param text the line to print     */    public static void printReceiptLine(String text)    {        receiptPrinter.println(text);    }        /** Simulate printing a line to the log     *     *  @param text the line to print     */    public static void printLogLine(String text)    {        gui.printLogLine(text);    }        /** Simulate sending a message to bank     *     *  @param message the message to send     *  @param balances (out) balances in customer's account as reported     *         by bank     *  @return status code returned by bank     */    public static void sendMessage(BroadcastMessage message, BroadcastBalances balances)    {        // Simulate time taken to send message over network        try        {            Thread.sleep(1 * 100);        }        catch(InterruptedException e){e.printStackTrace();}                eventBus.post(new SendMessageEvent(simulatedBank.handleMessage(message, balances)));    }    /** Notify the ATM that the state of the on-off switch has been changed     *     *  @param on true if state is now "on", false if it is "off"     */    void switchChanged(boolean on)    {        // The card reader is only enabled when the switch is on                cardReader.setVisible(on);                if (on)            atm.switchOn();        else            atm.switchOff();    }        /** Notify ATM that a card has been inserted     */    void cardInserted()    {        atm.cardInserted();    }        /** Accessor for GUI Panel that simulates the ATM     *     *  @return the GUI Panel     */    public GUI getGUI()    {        return gui;    }        /** Accessor for simulated bank     *     *  @return simulated bank     */    public SimulatedBank getSimulatedBank()    {        return simulatedBank;    }    /* Possible values for mode parameter to readInput() */        /** Read input in PIN mode - allow user to enter several characters,     *  and to clear the line if the user wishes; echo as asterisks     */    public static final int PIN_MODE = 1;        /** Read input in amount mode - allow user to enter several characters,     *  and to clear the line if the user wishes; echo what use types     */    public static final int AMOUNT_MODE = 2;        /** Read input in menu choice mode - wait for one digit key to be pressed,     *  and return value immediately.     */    public static final int MENU_MODE = 3;        /** The ATM object for the ATM being simulated     */    private static ATM atm;        /** The simulated operator panel     */    private static SimOperatorPanel operatorPanel;        /** The simulated card reader     */    private static SimCardReader cardReader;        /** The simulated display     */    private static SimDisplay display;        /** The simulated keyboard     */    private static SimKeyboard keyboard;        /** The simulated cash dispenser     */    private static SimCashDispenser cashDispenser;        /** The simulated envelope acceptor     */    private static SimEnvelopeAcceptor envelopeAcceptor;        /** The simulated receipt printer     */    private static SimReceiptPrinter receiptPrinter;        /** Panel containing the GUI that simulates the ATM     */    private static GUI gui;        /** Simulated bank     */    private static SimulatedBank simulatedBank;        /** The one and only instance of this class     */    private static Simulation theInstance;}    